    <style>.osSwitch {
        position: relative;
        display: inline-block;
        width: 34px;
        height: 15.3px;
      }
      .osSwitch input {
        opacity: 0;
        width: 0;
        height: 0;
      }
      .osSlider {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        border-radius: 34px;
        background-color: #93a0b5;
        transition: 0.4s;
      }
      .osSlider:before {
        position: absolute;
        content: "";
        height: 13px;
        width: 13px;
        left: 2px;
        bottom: 1px;
        border-radius: 50%;
        background-color: white;
        transition: 0.4s;
      }
      input:checked + .sliderGreen {
        background-color: #04d289;
      }
      input:checked + .sliderRed {
        background-color: #ff3b30;
      }
      input:not(:checked) + .defaultGreen {
        background-color: #04d289;
      }
      input:checked + .osSlider:before {
        transform: translateX(17px);
      }
    </style>
