    <style>.line-animation {
        position: relative;
        width: 100%;
        height: 10px; /* Adjust the height of the line as needed */
        background-color: #f0f0f0; /* Background color for the line */
        overflow: hidden;
      }

      .dot {
        position: absolute;
        left: 0;
        top: 0;
        width: 10px;
        height: 10px;
        background-color: blue; /* Dot color */
        border-radius: 50%;
        animation: moveDot 2s linear infinite; /* Animation */
      }

      @keyframes moveDot {
        0% {
          left: 0;
        }
        50% {
          left: 100%;
        }
        100% {
          left: 0;
        }
      }
    </style>

